mapper.select:
SELECT zo.user_id, zo.upload_id, zo.equipment_id, zo.save_time, zo.read_duration, zo.read_distance, zo.read_light, zo.look_phone_duration, zo.look_tv_computer_duration, zo.sit_tilt, zo.outdoors_duration, tur.remindsNum ,tut.userDurtion from ( SELECT tuj.user_id, tuj.upload_id, tuj.equipment_id, date_format(tuj.save_time, '%Y-%m-%d') save_time, SUM(IFNULL(tuj.read_duration, 0.0)) / count(read_duration) read_duration, SUM(IFNULL(tuj.read_distance, 0.0)) / count(read_distance) read_distance, SUM(IFNULL(tuj.read_light, 0.0)) / count(read_light) read_light, SUM(IFNULL(tuj.look_phone_duration, 0.0)) / SUM(tuj.look_phone_count) look_phone_duration, SUM(IFNULL(tuj.look_tv_computer_duration,0.0)) / SUM(tuj.look_tv_computer_count) look_tv_computer_duration, SUM(IFNULL(tuj.sit_tilt, 0.0)) / count(sit_tilt) sit_tilt, sum(if(tuj.status=2,outdoors_duration,0)) outdoors_duration FROM t_use_jianhuyi_log tuj GROUP BY substr(tuj.save_time, 1, 10),tuj.user_id ORDER BY substr(tuj.save_time, 1, 10) DESC ) zo LEFT JOIN ( SELECT upload_id, user_id, date_format(reminds_time, '%Y-%m-%d') reminds_time, COUNT(*) remindsNum FROM t_use_reminds GROUP BY substr(reminds_time, 1, 10),user_id ) tur on zo.user_id = tur.user_id and zo.save_time = tur.reminds_time LEFT JOIN ( SELECT `id`, date_format(use_time, '%Y-%m-%d') use_time, `save_time`, user_id, upload_id, COUNT(*)/20 userDurtion FROM t_use_time GROUP BY substr(use_time, 1, 10),user_id )tut on zo.user_id = tut.user_id and zo.save_time = tut.use_time
and zo.upload_id = #{uploadId} and zo.user_id = #{userId} and zo.save_time >= #{startTime} and zo.save_time <= #{endTime}
limit #{offset}, #{limit}

select id, user_id, add_time, equipment_id, save_time, read_duration, outdoors_duration, read_distance, read_light, look_phone_duration, look_phone_count, look_tv_computer_duration, look_tv_computer_count, sit_tilt, use_jianhuyi_duration, sport_duration, `del_flag` from t_use_jianhuyi_log where save_time >= date_sub(#{saveTime}, INTERVAL - 0 DAY) AND save_time < date_sub(#{saveTime}, INTERVAL -1 DAY) and user_id = #{userId}
order by ${sort} ${order}
order by id desc
limit #{offset}, #{limit}

select
id,
user_id,
add_time,
equipment_id,
save_time,
SUM(read_duration)/count(*) read_duration,
SUM(outdoors_duration)/count(*) outdoors_duration,
SUM(read_distance)/count(*) read_distance,
SUM(read_light)/count(*) read_light,
SUM(look_phone_duration)/count(*) look_phone_duration,
SUM(look_phone_count)/count(*) look_phone_count,
SUM(look_tv_computer_duration)/count(*) look_tv_computer_duration,
SUM(look_tv_computer_count)/count(*) look_tv_computer_count,
SUM(sit_tilt)/count(*) sit_tilt,
SUM(use_jianhuyi_duration)/count(*) use_jianhuyi_duration,
SUM(sport_duration)/count(*) sport_duration,
`del_flag`
from t_use_jianhuyi_log

    and id = #{id}
    and user_id = #{userId}
    and add_time = #{addTime}
    and save_time like CONCAT('%',#{saveTime},'%')
    and add_time >= #{startTime}
    and add_time <= #{endTime}
    and read_duration = #{readDuration}
    and outdoors_duration = #{outdoorsDuration}

    and read_distance = #{readDistance}
    and read_light = #{readLight}
    and look_phone_duration =
        #{lookPhoneDuration}

    and look_tv_computer_duration =
        #{lookTvComputerDuration}

    and sit_tilt = #{sitTilt}
    and use_jianhuyi_duration =
        #{useJianhuyiDuration}

    and sport_duration = #{sportDuration}
    and del_flag = #{delFlag}

    and YEARWEEK(date_format(save_time,'%Y-%m-%d'),1) =
        YEARWEEK(now(),1)

    and DATE_FORMAT( save_time, '%Y%m') = DATE_FORMAT( CURDATE( ) ,
        '%Y%m' )

    and QUARTER(save_time)=QUARTER(now())
    and YEAR(save_time)=YEAR(NOW())


GROUP BY date_format(save_time, '%Y-%m-%d')


        order by ${sort} ${order}


        order by save_time ASC



    limit #{offset}, #{limit}
