mapper.select:
select
id,
user_id,
add_time,
equipment_id,
save_time,
SUM(read_duration)/count(*) read_duration,
SUM(outdoors_duration)/count(*) outdoors_duration,
SUM(read_distance)/count(*) read_distance,
SUM(read_light)/count(*) read_light,
SUM(look_phone_duration)/count(*) look_phone_duration,
SUM(look_phone_count)/count(*) look_phone_count,
SUM(look_tv_computer_duration)/count(*) look_tv_computer_duration,
SUM(look_tv_computer_count)/count(*) look_tv_computer_count,
SUM(sit_tilt)/count(*) sit_tilt,
SUM(use_jianhuyi_duration)/count(*) use_jianhuyi_duration,
SUM(sport_duration)/count(*) sport_duration,
`del_flag`
from t_use_jianhuyi_log

    and id = #{id}
    and user_id = #{userId}
    and add_time = #{addTime}
    and save_time like CONCAT('%',#{saveTime},'%')
    and add_time >= #{startTime}
    and add_time <= #{endTime}
    and read_duration = #{readDuration}
    and outdoors_duration = #{outdoorsDuration}

    and read_distance = #{readDistance}
    and read_light = #{readLight}
    and look_phone_duration =
        #{lookPhoneDuration}

    and look_tv_computer_duration =
        #{lookTvComputerDuration}

    and sit_tilt = #{sitTilt}
    and use_jianhuyi_duration =
        #{useJianhuyiDuration}

    and sport_duration = #{sportDuration}
    and del_flag = #{delFlag}

    and YEARWEEK(date_format(save_time,'%Y-%m-%d'),1) =
        YEARWEEK(now(),1)

    and DATE_FORMAT( save_time, '%Y%m') = DATE_FORMAT( CURDATE( ) ,
        '%Y%m' )

    and QUARTER(save_time)=QUARTER(now())
    and YEAR(save_time)=YEAR(NOW())


GROUP BY date_format(save_time, '%Y-%m-%d')


        order by ${sort} ${order}


        order by save_time desc



    limit #{offset}, #{limit}

select id, user_id, add_time, equipment_id, save_time, read_duration, outdoors_duration, read_distance, read_light, look_phone_duration, look_phone_count, look_tv_computer_duration, look_tv_computer_count, sit_tilt, use_jianhuyi_duration, sport_duration, `del_flag` from t_use_jianhuyi_log where save_time >= date_sub(#{saveTime}, INTERVAL - 0 DAY) AND save_time < date_sub(#{saveTime}, INTERVAL -1 DAY) and user_id = #{userId}
order by ${sort} ${order}
order by id desc
limit #{offset}, #{limit}

select
id,
user_id,
add_time,
equipment_id,
save_time,
SUM(read_duration)/count(*) read_duration,
SUM(outdoors_duration)/count(*) outdoors_duration,
SUM(read_distance)/count(*) read_distance,
SUM(read_light)/count(*) read_light,
SUM(look_phone_duration)/count(*) look_phone_duration,
SUM(look_phone_count)/count(*) look_phone_count,
SUM(look_tv_computer_duration)/count(*) look_tv_computer_duration,
SUM(look_tv_computer_count)/count(*) look_tv_computer_count,
SUM(sit_tilt)/count(*) sit_tilt,
SUM(use_jianhuyi_duration)/count(*) use_jianhuyi_duration,
SUM(sport_duration)/count(*) sport_duration,
`del_flag`
from t_use_jianhuyi_log

    and id = #{id}
    and user_id = #{userId}
    and add_time = #{addTime}
    and save_time like CONCAT('%',#{saveTime},'%')
    and add_time >= #{startTime}
    and add_time <= #{endTime}
    and read_duration = #{readDuration}
    and outdoors_duration = #{outdoorsDuration}

    and read_distance = #{readDistance}
    and read_light = #{readLight}
    and look_phone_duration =
        #{lookPhoneDuration}

    and look_tv_computer_duration =
        #{lookTvComputerDuration}

    and sit_tilt = #{sitTilt}
    and use_jianhuyi_duration =
        #{useJianhuyiDuration}

    and sport_duration = #{sportDuration}
    and del_flag = #{delFlag}

    and YEARWEEK(date_format(save_time,'%Y-%m-%d'),1) =
        YEARWEEK(now(),1)

    and DATE_FORMAT( save_time, '%Y%m') = DATE_FORMAT( CURDATE( ) ,
        '%Y%m' )

    and QUARTER(save_time)=QUARTER(now())
    and YEAR(save_time)=YEAR(NOW())


GROUP BY date_format(save_time, '%Y-%m-%d')


        order by ${sort} ${order}


        order by save_time ASC



    limit #{offset}, #{limit}

select count(*) from (
SELECT
count(*)
FROM
t_use_jianhuyi_log

    and id = #{id}
    and user_id = #{userId}
    and add_time = #{addTime}
    and read_duration = #{readDuration}
    and outdoors_duration = #{outdoorsDuration}

    and read_distance = #{readDistance}
    and read_light = #{readLight}
    and look_phone_duration =
        #{lookPhoneDuration}

    and look_tv_computer_duration =
        #{lookTvComputerDuration}

    and sit_tilt = #{sitTilt}
    and use_jianhuyi_duration =
        #{useJianhuyiDuration}

    and sport_duration = #{sportDuration}
    and del_flag = #{delFlag}
    and YEARWEEK(date_format(save_time,'%Y-%m-%d'),1) =
        YEARWEEK(now(),1)


GROUP BY
date_format(save_time, '%Y-%m-%d')
) t_use_jianhuyi_log
