mapper.select:
select
`id`,`user_id`,`open_id`,`nickname`,`password`,`phone`,`heard_url`,`name`,`identity_card`,`unionid`,`register_time`,`pay_num`,`serve_num`,`balance`,`restitution`,`pay_time`,`login_time`,`add_time`,`update_time`,`delete_flag`,`username`,`sex`,`birthday`,`flag`,`age`,`school`,`grade`,student_num,`L_vision`,`R_vision`,`L_eye_ball_diameter`,`R_eye_ball_diameter`,`L_column_diameter`,`R_column_diameter`,`L_eye_axis`,`R_eye_axis`
from t_user

    and id = #{id}
    and user_id = #{userId}
    and open_id = #{openId}
    and nickname = #{nickname}
    and password = #{password}
    and phone = #{phone}
    and heard_url = #{heardUrl}
    and name = #{name}
    and identity_card = #{identityCard}
    and unionid = #{unionid}
    and register_time = #{registerTime}
    and pay_num = #{payNum}
    and serve_num = #{serveNum}
    and balance = #{balance}
    and restitution = #{restitution}
    and pay_time = #{payTime}
    and login_time = #{loginTime}
    and add_time = #{addTime}
    and update_time = #{updateTime}
    and delete_flag = #{deleteFlag}
    and username = #{username}
    and sex = #{sex}
    and birthday = #{birthday}
    and flag = #{flag}
    and age = #{age}
    and school = #{school}
    and grade = #{grade}
    and L_vision = #{lVision}
    and R_vision = #{rVision}
    and L_eye_ball_diameter =
        #{lEyeBallDiameter}

    and R_eye_ball_diameter =
        #{rEyeBallDiameter}

    and L_column_diameter = #{lColumnDiameter}
    and R_column_diameter = #{rColumnDiameter}
    and L_eye_axis = #{lEyeAxis}
    and R_eye_axis = #{rEyeAxis}
    and delete_flag = 1



        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}

select count(*) from t_user

    and id = #{id}
