mapper.select:
select
`id`,`user_id`,save_time,`add_time`,`read_duration`,`outdoors_duration`,`read_distance`,`read_light`,`look_phone_duration`,`look_phone_count`,`look_tv_computer_count`,`look_tv_computer_duration`,`sit_tilt`,`use_jianhuyi_duration`,`sport_duration`,`del_flag`
from t_use_jianhuyi_log

    and id = #{id}
    and user_id = #{userId}
    and add_time = #{addTime}
    and read_duration = #{readDuration}
    and outdoors_duration = #{outdoorsDuration}

    and read_distance = #{readDistance}
    and read_light = #{readLight}
    and look_phone_duration =
        #{lookPhoneDuration}

    and look_tv_computer_duration =
        #{lookTvComputerDuration}

    and sit_tilt = #{sitTilt}
    and use_jianhuyi_duration =
        #{useJianhuyiDuration}

    and sport_duration = #{sportDuration}
    and del_flag = #{delFlag}



        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}

select count(*) from t_use_jianhuyi_log

    and id = #{id}
    and user_id = #{userId}
    and add_time = #{addTime}
    and read_duration = #{readDuration}
    and outdoors_duration = #{outdoorsDuration}

    and read_distance = #{readDistance}
    and read_light = #{readLight}
    and look_phone_duration =
        #{lookPhoneDuration}

    and look_tv_computer_duration =
        #{lookTvComputerDuration}

    and sit_tilt = #{sitTilt}
    and use_jianhuyi_duration =
        #{useJianhuyiDuration}

    and sport_duration = #{sportDuration}
    and del_flag = #{delFlag}
